import { useEffect } from "react";
import { useNavigate } from "react-router-dom";

const Dashboard = () => {
  const navigate = useNavigate();
  const userId = localStorage.getItem("user_id");
  const username = localStorage.getItem("username"); // Get username from localStorage

  useEffect(() => {
    if (!userId) {
      navigate("/login");
    }
  }, [navigate, userId]);

  const handleLogout = () => {
    localStorage.removeItem("user_id");
    localStorage.removeItem("username");
    window.location.href = "/login"; // Force redirect to login page
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white flex flex-col items-center">
      <nav className="bg-gray-800 w-full p-4 flex justify-between items-center">
        <h1 className="text-2xl font-bold">Crypto Dashboard</h1>
        <button onClick={handleLogout} className="bg-red-500 hover:bg-red-600 px-4 py-2 rounded-lg">
          Logout
        </button>
      </nav>
      {/* Profile Section */}
      <div className="bg-gray-800 p-6 mt-6 rounded-xl shadow-lg w-96 mx-auto flex items-center space-x-4">
        <img
          src="https://via.placeholder.com/100" // Dummy Profile Picture
          alt="Profile"
          className="w-16 h-16 rounded-full border border-gray-500"
        />
        <div>
          {user ? (
            <>
              <h3 className="text-xl font-semibold">{user.username}</h3>
              <p className="text-gray-300">User ID: {user.user_id}</p>
            </>
          ) : (
            <p className="text-gray-400">{message}</p>
          )}
        </div>
      </div>

      <div className="mt-16 text-center">
        <h2 className="text-3xl font-bold">Welcome, {username || "User"}!</h2>
        <p className="text-gray-300 mt-2">Manage your cryptocurrency wallets.</p>

        {/* Wallet Button - Placed Under Greeting */}
        <button
          onClick={() => navigate("/wallet")}
          className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-3 px-6 rounded-lg mt-6 text-lg"
        >
          Go to Wallets
        </button>
      </div>
    </div>
  );
};

export default Dashboard;
