import { useEffect } from "react";
import { useNavigate } from "react-router-dom";

// NOTE: If you want to use custom animations like "animate-fadeIn",
// make sure to add them in your Tailwind configuration or import an animation library.

const Dashboard = () => {
  const navigate = useNavigate();
  const userId = localStorage.getItem("user_id");
  const username = localStorage.getItem("username");

  useEffect(() => {
    if (!userId) {
      navigate("/login");
    }
  }, [navigate, userId]);

  const handleLogout = () => {
    localStorage.removeItem("user_id");
    localStorage.removeItem("username");
    window.location.href = "/login";
  };

  return (
    <div
      className="min-h-screen bg-gradient-to-br from-gray-900 to-gray-800 text-white flex flex-col items-center
                 animate-fadeIn"  // (Optional) fadeIn animation - ensure it's defined in your tailwind config
    >
      {/* Navigation Bar */}
      <nav className="w-full bg-black bg-opacity-50 backdrop-blur-md p-4 flex justify-between items-center shadow-lg">
        <h1 className="text-3xl font-extrabold tracking-wide drop-shadow-lg">
          Crypto Dashboard
        </h1>
        <button
          onClick={handleLogout}
          className="bg-red-600 hover:bg-red-700 transition-transform duration-200 transform hover:scale-105 px-4 py-2 rounded-lg shadow"
        >
          Logout
        </button>
      </nav>

      {/* Profile Section */}
      <div className="mt-10 bg-black bg-opacity-50 backdrop-blur-lg p-8 rounded-xl shadow-2xl w-96 flex items-center space-x-6 border border-gray-700">
        <img
          src="https://via.placeholder.com/80"
          alt="Profile"
          className="w-20 h-20 rounded-full border-2 border-white shadow-lg"
        />
        <div>
          <h3 className="text-2xl font-bold tracking-wide">
            {username || "User"}
          </h3>
          <p className="text-gray-300 mt-1">User ID: {userId || "N/A"}</p>
        </div>
      </div>

      {/* Dashboard Welcome Text */}
      <div className="mt-10 text-center">
        <h2 className="text-4xl font-bold tracking-wide drop-shadow-md">
          Welcome, {username || "User"}!
        </h2>
        <p className="text-gray-400 mt-2 text-lg">
          Manage your cryptocurrency wallets with style.
        </p>
        <button
          onClick={() => navigate("/wallet")}
          className="bg-gradient-to-r from-blue-700 to-blue-500 hover:from-blue-800 hover:to-blue-600 transition-all duration-300 text-white font-semibold py-3 px-8 rounded-full shadow-xl mt-8 text-lg"
        >
          Go to Wallets
        </button>
        <br />
        <button
          onClick={() => navigate("/transactions")}
          className="bg-gradient-to-r from-green-700 to-green-500 hover:from-green-800 hover:to-green-600 transition-all duration-300 text-white font-semibold py-3 px-8 rounded-full shadow-xl mt-4 text-lg"
        >
          View Transactions
        </button>
      </div>
    </div>
  );
};

export default Dashboard;
