import { useEffect } from "react";
import { useNavigate } from "react-router-dom";

const Dashboard = () => {
  const navigate = useNavigate();
  const userId = localStorage.getItem("user_id");
  const username = localStorage.getItem("username");

  useEffect(() => {
    if (!userId) {
      navigate("/login");
    }
  }, [navigate, userId]);

  const handleLogout = () => {
    localStorage.removeItem("user_id");
    localStorage.removeItem("username");
    window.location.href = "/login";
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-100 to-gray-200 text-gray-800 flex flex-col items-center p-8">
      {/* ðŸ”¹ Navigation Bar */}
      <nav className="bg-white bg-opacity-80 backdrop-filter backdrop-blur-lg w-full p-5 flex justify-between items-center rounded-xl shadow-lg border border-gray-200">
        <h1 className="text-2xl font-semibold text-indigo-700 tracking-wide">
          <span className="text-indigo-500">ðŸš€</span> CryptoX Exchange
        </h1>
        <button
          onClick={handleLogout}
          className="bg-red-600 hover:bg-red-700 px-5 py-2 rounded-lg shadow-md text-white font-medium transition-colors duration-300"
        >
          Logout
        </button>
      </nav>

      {/* ðŸ”¹ Profile Card */}
      <div className="mt-12 bg-white bg-opacity-80 backdrop-filter backdrop-blur-lg p-8 rounded-2xl shadow-xl border border-gray-200 max-w-md w-full flex items-center space-x-6">
        <div className="relative">
          <img
            src="https://via.placeholder.com/100"
            alt="Profile"
            className="w-20 h-20 rounded-full border-4 border-indigo-300 object-cover"
          />
          <div className="absolute inset-0 rounded-full border-4 border-indigo-500 animate-pulse opacity-50"></div>
        </div>
        <div>
          <h3 className="text-2xl font-semibold text-indigo-700 mb-1">
            {username || "User"}
          </h3>
          <p className="text-gray-600 text-sm">User ID: {userId || "N/A"}</p>
        </div>
      </div>

      {/* ðŸ”¹ Quick Access Buttons */}
      <div className="mt-16 flex flex-col space-y-6 w-full max-w-md">
        <button
          onClick={() => navigate("/wallet")}
          className="bg-indigo-600 hover:bg-indigo-700 text-white font-semibold py-4 px-8 rounded-xl shadow-lg transition-colors duration-300 transform hover:scale-105"
        >
          View Wallets
        </button>
        <button
          onClick={() => navigate("/transactions")}
          className="bg-gradient-to-r from-purple-500 to-indigo-600 hover:from-purple-600 hover:to-indigo-700 text-white font-semibold py-4 px-8 rounded-xl shadow-lg transition-colors duration-300 transform hover:scale-105"
        >
          Transactions
        </button>
      </div>
    </div>
  );
};

export default Dashboard;
