import { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion"; // Import Framer Motion

const Dashboard = () => {
  const navigate = useNavigate();
  const userId = localStorage.getItem("user_id");
  const username = localStorage.getItem("username");

  useEffect(() => {
    if (!userId) {
      navigate("/login");
    }
  }, [navigate, userId]);

  const handleLogout = () => {
    localStorage.removeItem("user_id");
    localStorage.removeItem("username");
    window.location.href = "/login";
  };

  const navVariants = {
    hidden: { opacity: 0, y: -20 },
    visible: { opacity: 1, y: 0, transition: { duration: 0.6 } },
  };

  const profileVariants = {
    hidden: { opacity: 0, scale: 0.8 },
    visible: { opacity: 1, scale: 1, transition: { duration: 0.8, ease: "easeInOut" } },
  };

  const buttonVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0, transition: { duration: 0.7, delay: 0.2 } },
    hover: { scale: 1.05, transition: { duration: 0.3 } },
    tap: { scale: 0.95 },
  };

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
      className="min-h-screen bg-gradient-to-br from-indigo-100 to-purple-100 text-gray-800 flex flex-col items-center p-8"
    >
      {/* ðŸ”¹ Navigation Bar */}
      <motion.nav
        variants={navVariants}
        initial="hidden"
        animate="visible"
        className="bg-white bg-opacity-80 backdrop-filter backdrop-blur-lg w-full p-5 flex justify-between items-center rounded-xl shadow-xl border border-gray-200"
      >
        <h1 className="text-2xl font-semibold text-indigo-700 tracking-wide">
          <span className="text-indigo-500 animate-pulse">ðŸš€</span> CryptoX Exchange
        </h1>
        <motion.button
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          onClick={handleLogout}
          className="bg-red-600 hover:bg-red-700 px-5 py-2 rounded-lg shadow-md text-white font-medium transition-colors duration-300"
        >
          Logout
        </motion.button>
      </motion.nav>

      {/* ðŸ”¹ Profile Card */}
      <motion.div
        variants={profileVariants}
        initial="hidden"
        animate="visible"
        className="mt-12 bg-white bg-opacity-80 backdrop-filter backdrop-blur-lg p-8 rounded-2xl shadow-xl border border-gray-200 max-w-md w-full flex items-center space-x-6"
      >
        <div className="relative">
          <img
            src="https://via.placeholder.com/100"
            alt="Profile"
            className="w-20 h-20 rounded-full border-4 border-indigo-300 object-cover"
          />
          <div className="absolute inset-0 rounded-full border-4 border-indigo-500 animate-ping opacity-50"></div>
        </div>
        <div>
          <h3 className="text-2xl font-semibold text-indigo-700 mb-1">
            {username || "User"}
          </h3>
          <p className="text-gray-600 text-sm">User ID: {userId || "N/A"}</p>
        </div>
      </motion.div>

      {/* ðŸ”¹ Quick Access Buttons */}
      <div className="mt-16 flex flex-col space-y-6 w-full max-w-md">
        <motion.button
          variants={buttonVariants}
          initial="hidden"
          animate="visible"
          whileHover="hover"
          whileTap="tap"
          onClick={() => navigate("/wallet")}
          className="bg-gradient-to-r from-indigo-600 to-blue-600 text-white font-semibold py-4 px-8 rounded-xl shadow-lg transition-colors duration-300"
        >
          View Wallets
        </motion.button>
        <motion.button
          variants={buttonVariants}
          initial="hidden"
          animate="visible"
          whileHover="hover"
          whileTap="tap"
          onClick={() => navigate("/transactions")}
          className="bg-gradient-to-r from-purple-600 to-pink-600 text-white font-semibold py-4 px-8 rounded-xl shadow-lg transition-colors duration-300"
        >
          Transactions
        </motion.button>
      </div>
    </motion.div>
  );
};

export default Dashboard;
