import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Dashboard = () => {
  const navigate = useNavigate();
  const [wallets, setWallets] = useState([]);
  const [message, setMessage] = useState("");
  const userId = localStorage.getItem("user_id");
  const username = localStorage.getItem("username"); // Get username from localStorage

  useEffect(() => {
    if (!userId) {
      navigate("/login");
    } else {
      const fetchWallets = async () => {
        try {
          const response = await axios.post("http://localhost/Crypto/get_wallets.php", { user_id: userId });
          if (response.data.status === "success" && Array.isArray(response.data.wallets)) {
            setWallets(response.data.wallets);
          } else {
            setMessage(response.data.message || "No wallets found.");
          }
        } catch (error) {
          setMessage("❌ Error fetching wallets.");
        }
      };
      fetchWallets();
    }
  }, [navigate, userId]);

  const handleLogout = () => {
    localStorage.removeItem("user_id");
    localStorage.removeItem("username");
    navigate("/login");
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white">
      <nav className="bg-gray-800 p-4 flex justify-between items-center">
        <h1 className="text-2xl font-bold">Crypto Dashboard</h1>
        <button onClick={handleLogout} className="bg-red-500 hover:bg-red-600 px-4 py-2 rounded-lg">
          Logout
        </button>
      </nav>

      <div className="flex flex-col items-center mt-10">
        <h2 className="text-3xl font-bold">Welcome, {username || "User"}!</h2>
        <p className="text-gray-300 mt-2">Manage your cryptocurrency wallets.</p>

        <div className="bg-gray-800 p-6 mt-6 rounded-xl shadow-lg w-96">
          <h3 className="text-xl font-semibold text-center">Your Wallets</h3>
          {wallets.length > 0 ? (
            <ul className="mt-4">
              {wallets.map((wallet) => (
                <li key={wallet.wallet_id} className="p-3 bg-gray-700 rounded-lg mt-2">
                  {wallet.currency_type} - Balance: ${wallet.balance}
                </li>
              ))}
            </ul>
          ) : (
            <p className="text-center mt-4 text-gray-400">No wallets found.</p>
          )}
        </div>

        {message && <p className="mt-4 text-gray-300">{message}</p>}
      </div>
    </div>
  );
};

export default Dashboard;
