import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { motion, AnimatePresence } from "framer-motion";
import {
  FaArrowLeft,
  FaCheckCircle,
  FaTimesCircle,
  FaSyncAlt,
} from "react-icons/fa";
import { format } from "date-fns";

// Custom hook for data fetching
const useTransactions = (userId) => {
  const [state, setState] = useState({
    transactions: [],
    isLoading: true,
    error: null,
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.post(
          "http://localhost/Crypto/get_transactions.php",
          { user_id: userId }
        );

        if (response.data.status === "success") {
          setState({
            transactions: response.data.transactions,
            isLoading: false,
            error: response.data.transactions.length
              ? null
              : "No transactions found.",
          });
        } else {
          setState({
            ...state,
            isLoading: false,
            error: "No transactions available.",
          });
        }
      } catch (error) {
        setState({
          transactions: [],
          isLoading: false,
          error: "❌ Failed to load transactions. Please try again.",
        });
      }
    };

    userId
      ? fetchData()
      : setState({ ...state, isLoading: false, error: "❌ User not authenticated." });
  }, [userId]);

  return state;
};

// Reusable components
const LoadingSpinner = () => (
  <motion.div
    initial={{ opacity: 0 }}
    animate={{ opacity: 1 }}
    exit={{ opacity: 0 }}
    className="flex items-center space-x-3 text-gray-300 mb-4"
  >
    <FaSyncAlt className="animate-spin text-3xl" />
    <span>Loading transactions...</span>
  </motion.div>
);

const StatusPill = ({ type, children }) => {
  const typeStyles = {
    Send: "bg-red-600 text-white",
    Receive: "bg-green-600 text-white",
    default: "bg-yellow-600 text-black",
  };

  return (
    <div
      className={`absolute top-4 right-4 px-4 py-2 flex items-center gap-2 rounded-full text-sm font-bold shadow-lg ${typeStyles[type] || typeStyles.default}`}
    >
      {children}
    </div>
  );
};

const TransactionCard = ({ transaction }) => {
  const typeConfig = {
    Send: { emoji: "📤", color: "bg-red-100" },
    Receive: { emoji: "📥", color: "bg-green-100" },
    default: { emoji: "💰", color: "bg-yellow-100" },
  };

  const { emoji, color } =
    typeConfig[transaction.transaction_type] || typeConfig.default;

  return (
    <motion.div
      variants={{
        hidden: { opacity: 0, y: 20 },
        visible: { opacity: 1, y: 0, transition: { duration: 0.4 } },
        hover: { scale: 1.05, boxShadow: "0 10px 30px rgba(0, 0, 0, 0.3)" },
      }}
      whileHover="hover"
      className={`relative bg-gray-800 p-6 rounded-lg shadow-lg border border-gray-700 overflow-hidden group ${color}`}
    >
      <StatusPill type={transaction.transaction_type}>
        {emoji} {transaction.transaction_type}
      </StatusPill>

      <div className="text-gray-100 space-y-2">
        <p className="text-xl font-bold">
          {transaction.currency_type} Transaction
        </p>
        <p
          className={`font-semibold flex items-center gap-2 ${
            transaction.status === "Completed"
              ? "text-green-400"
              : "text-red-400"
          }`}
        >
          {transaction.status === "Completed" ? <FaCheckCircle /> : <FaTimesCircle />}
          <span>Status: {transaction.status}</span>
        </p>
        <p className="text-2xl font-extrabold text-yellow-300">
          {emoji} {transaction.amount} {transaction.currency_type}
        </p>
        <p className="text-gray-400 text-sm">
          <strong>Time:</strong> {format(new Date(transaction.timestamp), "PPpp")}
        </p>
      </div>
    </motion.div>
  );
};

const Transactions = () => {
  const navigate = useNavigate();
  const userId = localStorage.getItem("user_id");
  const { transactions, isLoading, error } = useTransactions(userId);

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
      className="min-h-screen bg-gradient-to-br from-gray-900 to-black text-white p-8 flex flex-col items-center"
    >
      <motion.button
        whileHover={{ scale: 1.05 }}
        whileTap={{ scale: 0.95 }}
        onClick={() => navigate("/dashboard")}
        className="bg-gradient-to-r from-blue-600 to-purple-700 hover:opacity-90 transition-transform px-6 py-3 rounded-full shadow-lg mb-6 text-lg font-semibold tracking-wide flex items-center space-x-2"
      >
        <FaArrowLeft /> <span>Back to Dashboard</span>
      </motion.button>

      <h2 className="text-5xl font-extrabold text-center bg-clip-text text-transparent bg-gradient-to-r from-yellow-500 via-red-500 to-pink-500 drop-shadow-lg tracking-wide mb-8">
        Transaction History
      </h2>

      <AnimatePresence>
        {isLoading ? (
          <LoadingSpinner />
        ) : (
          <>
            {error && (
              <p className="text-center mt-4 text-gray-400">{error}</p>
            )}

            {transactions.length > 0 && (
              <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ delayChildren: 0.2, staggerChildren: 0.1 }}
                className="mt-8 w-full max-w-5xl grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8"
              >
                {transactions.map((tx) => (
                  <TransactionCard key={tx.transaction_id} transaction={tx} />
                ))}
              </motion.div>
            )}
          </>
        )}
      </AnimatePresence>
    </motion.div>
  );
};

export default Transactions;
