import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Transactions = () => {
  const navigate = useNavigate();
  const userId = localStorage.getItem("user_id");
  const [transactions, setTransactions] = useState([]);
  const [message, setMessage] = useState("Loading transactions...");

  useEffect(() => {
    const userId = localStorage.getItem("user_id");
    console.log("User ID from localStorage:", userId); // üîç Check if user_id exists
  
    if (!userId) {
      setMessage("‚ùå No user found. Please log in.");
      return;
    }
  
    fetchTransactions(userId); // Pass user_id directly
  }, []);
  

  const fetchTransactions = async () => {
    try {
      const response = await axios.post("http://localhost/Crypto/get_transactions.php", { user_id: userId });

      if (response.data.status === "success" && response.data.transactions.length > 0) {
        setTransactions(response.data.transactions);
        setMessage("");
      } else {
        setMessage("No transactions found.");
      }
    } catch (error) {
      console.error("Error fetching transactions:", error);
      setMessage("‚ùå Error fetching transactions.");
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 to-black text-white p-6 flex flex-col items-center">
      {/* Back Button */}
      <button 
        onClick={() => navigate("/dashboard")} 
        className="bg-gradient-to-r from-purple-600 to-indigo-500 hover:opacity-80 transition-all px-6 py-2 rounded-xl shadow-md mb-6"
      >
        ‚¨Ö Back to Dashboard
      </button>

      {/* Page Title */}
      <h2 className="text-4xl font-extrabold text-center bg-clip-text text-transparent bg-gradient-to-r from-blue-400 to-teal-300">
        Transaction History
      </h2>

      {message && <p className="text-center mt-4 text-gray-400">{message}</p>}

      {/* Transaction List */}
      {transactions.length > 0 && (
        <div className="mt-6 w-full max-w-3xl">
          {transactions.map((tx) => (
            <div 
              key={tx.transaction_id} 
              className="bg-white/10 backdrop-blur-lg p-5 rounded-xl shadow-lg mb-4 border border-gray-700 hover:scale-105 transition-transform"
            >
              <p className="text-lg">
                <span className="font-semibold">Type:</span> 
                <span className={`ml-2 px-3 py-1 rounded-lg ${
                  tx.transaction_type === "Send" ? "bg-red-500 text-white" : 
                  tx.transaction_type === "Receive" ? "bg-green-500 text-white" : 
                  "bg-yellow-500 text-black"
                }`}>
                  {tx.transaction_type}
                </span>
              </p>
              <p className={`font-semibold ${tx.status === "Completed" ? "text-green-400" : "text-red-400"}`}>
                Status: {tx.status}
              </p>
              <p className="text-xl font-bold text-yellow-300">üí∞ {tx.amount} {tx.currency_type}</p>
              <p className="text-gray-400 text-sm"><strong>Time:</strong> {tx.timestamp}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Transactions;
