import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Dashboard = () => {
  const navigate = useNavigate();
  const userId = localStorage.getItem("user_id");
  const username = localStorage.getItem("username");

  const [walletBalance, setWalletBalance] = useState(null);
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    if (!userId) {
      navigate("/login");
    } else {
      fetchDashboardData();
    }
  }, [navigate, userId]);

  const fetchDashboardData = async () => {
    try {
      // Fetch Wallet Balance
      const walletRes = await axios.post("http://localhost/Crypto/get_wallet_balance.php", { user_id: userId });
      if (walletRes.data.status === "success") {
        setWalletBalance(walletRes.data.balance);
      } else {
        setWalletBalance(0);
      }

      // Fetch Transactions
      const txRes = await axios.post("http://localhost/Crypto/get_transactions.php", { user_id: userId });
      if (txRes.data.status === "success") {
        setTransactions(txRes.data.transactions);
      }

      setLoading(false);
    } catch (err) {
      console.error("Error fetching dashboard data:", err);
      setError("âŒ Failed to load dashboard data.");
      setLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("user_id");
    localStorage.removeItem("username");
    window.location.href = "/login"; // Force redirect to login page
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white flex flex-col items-center p-6">
      {/* ðŸ”¹ Navigation Bar */}
      <nav className="bg-gray-800 w-full p-4 flex justify-between items-center rounded-lg shadow-lg">
        <h1 className="text-2xl font-extrabold text-yellow-400">ðŸš€ CryptoX Exchange</h1>
        <button 
          onClick={handleLogout} 
          className="bg-red-500 hover:bg-red-600 px-4 py-2 rounded-lg shadow-md transition-all"
        >
          Logout
        </button>
      </nav>

      {/* ðŸ”¹ Profile Card */}
      <div className="mt-10 bg-white/10 backdrop-blur-lg p-6 rounded-xl shadow-xl border border-gray-700 max-w-md w-full flex items-center space-x-4">
        <img
          src="https://via.placeholder.com/80" // Dummy Profile Picture
          alt="Profile"
          className="w-16 h-16 rounded-full border border-gray-500"
        />
        <div>
          <h3 className="text-xl font-bold text-blue-300">{username || "User"}</h3>
          <p className="text-gray-400 text-sm">User ID: {userId || "N/A"}</p>
        </div>
      </div>

      {/* ðŸ”¹ Wallet Balance */}
      <div className="mt-6 bg-gradient-to-r from-purple-600 to-indigo-500 p-6 rounded-xl shadow-xl max-w-md w-full text-center">
        <h2 className="text-2xl font-bold text-white">ðŸ’° Wallet Balance</h2>
        {loading ? (
          <p className="text-gray-300 mt-2">Loading...</p>
        ) : (
          <p className="text-4xl font-extrabold mt-2 text-yellow-300">${walletBalance || "0.00"}</p>
        )}
      </div>

      {/* ðŸ”¹ Quick Access Buttons */}
      <div className="mt-8 flex space-x-4">
        <button 
          onClick={() => navigate("/wallet")} 
          className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-3 px-6 rounded-lg shadow-md transition-all transform hover:scale-105"
        >
          View Wallets
        </button>
        <button 
          onClick={() => navigate("/transactions")} 
          className="bg-green-500 hover:bg-green-600 text-white font-semibold py-3 px-6 rounded-lg shadow-md transition-all transform hover:scale-105"
        >
          Transactions
        </button>
      </div>

      {/* ðŸ”¹ Recent Transactions */}
      <div className="mt-10 max-w-3xl w-full">
        <h3 className="text-2xl font-bold text-center text-gray-300">ðŸ“Š Recent Transactions</h3>

        {loading ? (
          <p className="text-center mt-4 text-gray-400">Loading transactions...</p>
        ) : transactions.length > 0 ? (
          <div className="mt-4 space-y-4">
            {transactions.map((tx) => (
              <div key={tx.transaction_id} className="bg-white/10 backdrop-blur-lg p-4 rounded-lg border border-gray-700 flex justify-between">
                <div>
                  <p className="font-semibold">{tx.currency_type}</p>
                  <p className="text-sm text-gray-400">{tx.transaction_type} {tx.amount} {tx.currency_type}</p>
                </div>
                <p className={`font-bold ${tx.status === "Completed" ? "text-green-400" : "text-red-400"}`}>
                  {tx.status === "Completed" ? `+${tx.amount}` : `-${tx.amount}`}
                </p>
              </div>
            ))}
          </div>
        ) : (
          <p className="text-center mt-4 text-gray-400">No transactions found.</p>
        )}
      </div>

      {error && <p className="text-center text-red-400 mt-4">{error}</p>}
    </div>
  );
};

export default Dashboard;
