import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { motion, AnimatePresence } from "framer-motion";
import { FaArrowLeft, FaCheckCircle, FaTimesCircle, FaSyncAlt, FaSearch } from "react-icons/fa";
import { format } from "date-fns";

// Custom hook for data fetching
const useTransactions = (userId) => {
  const [state, setState] = useState({
    transactions: [],
    isLoading: true,
    error: null,
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.post(
          "http://localhost/Crypto/get_transactions.php",
          { user_id: userId }
        );

        if (response.data.status === "success") {
          setState({
            transactions: response.data.transactions,
            isLoading: false,
            error: response.data.transactions.length
              ? null
              : "No transactions found.",
          });
        } else {
          setState({
            ...state,
            isLoading: false,
            error: "No transactions available.",
          });
        }
      } catch (error) {
        setState({
          transactions: [],
          isLoading: false,
          error: "âŒ Failed to load transactions. Please try again.",
        });
      }
    };

    userId
      ? fetchData()
      : setState({ ...state, isLoading: false, error: "âŒ User not authenticated." });
  }, [userId]);

  return state;
};

// Transaction Summary Component
const TransactionSummary = ({ transactions }) => {
  const totalSent = transactions
    .filter(tx => tx.transaction_type === "Send")
    .reduce((acc, tx) => acc + parseFloat(tx.amount), 0);
  const totalReceived = transactions
    .filter(tx => tx.transaction_type === "Receive")
    .reduce((acc, tx) => acc + parseFloat(tx.amount), 0);
  const totalTransactions = transactions.length;

  return (
    <div className="bg-gray-800 p-6 rounded-lg shadow-lg text-white mb-8">
      <h3 className="text-2xl font-bold mb-4">Transaction Summary</h3>
      <p>Total Transactions: {totalTransactions}</p>
      <p>Total Sent: {totalSent.toFixed(2)} USD</p>
      <p>Total Received: {totalReceived.toFixed(2)} USD</p>
    </div>
  );
};

// Loading Spinner
const LoadingSpinner = () => (
  <motion.div
    initial={{ opacity: 0 }}
    animate={{ opacity: 1 }}
    exit={{ opacity: 0 }}
    className="flex items-center space-x-3 text-gray-400 mb-4"
  >
    <FaSyncAlt className="animate-spin text-3xl" />
    <span>Loading transactions...</span>
  </motion.div>
);

// Transaction Card Component
const TransactionCard = ({ transaction, onClick }) => {
  const typeConfig = {
    Send: { icon: "ðŸ“¤", bgColor: "bg-gradient-to-r from-red-400 to-red-600", textColor: "text-white" },
    Receive: { icon: "ðŸ“¥", bgColor: "bg-gradient-to-r from-green-400 to-green-600", textColor: "text-white" },
    default: { icon: "ðŸ’°", bgColor: "bg-gradient-to-r from-yellow-400 to-yellow-600", textColor: "text-black" },
  };

  const { icon, bgColor, textColor } =
    typeConfig[transaction.transaction_type] || typeConfig.default;

  return (
    <motion.div
      whileHover={{ scale: 1.05, boxShadow: "0 10px 20px rgba(0, 0, 0, 0.3)" }}
      onClick={onClick}
      className={`relative p-6 rounded-lg shadow-lg transition-transform duration-300 ${bgColor} text-center text-lg text-white cursor-pointer`}
    >
      <div className={`absolute top-4 right-4 px-3 py-1 rounded-full text-sm font-bold ${textColor}`}>
        {icon} {transaction.transaction_type}
      </div>

      <div className="space-y-2">
        <p className="text-2xl font-semibold">{transaction.currency_type} Transaction</p>
        <p className={`font-medium flex items-center justify-center gap-2 ${transaction.status === "Completed" ? "text-green-300" : "text-red-300"}`}>
          {transaction.status === "Completed" ? <FaCheckCircle /> : <FaTimesCircle />}
          <span>Status: {transaction.status}</span>
        </p>
        <p className="text-3xl font-bold">
          {icon} {transaction.amount} {transaction.currency_type}
        </p>
        <p className="text-gray-200 text-sm">
          <strong>Time:</strong> {format(new Date(transaction.timestamp), "PPpp")}
        </p>
      </div>
    </motion.div>
  );
};

// Search Bar Component
const SearchBar = ({ searchTerm, setSearchTerm }) => (
  <div className="flex items-center mb-6">
    <FaSearch className="text-gray-400 mr-2" />
    <input
      type="text"
      placeholder="Search transactions..."
      value={searchTerm}
      onChange={(e) => setSearchTerm(e.target.value)}
      className="p-2 rounded-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500"
    />
  </div>
);

// Transactions Component
const Transactions = () => {
  const navigate = useNavigate();
  const userId = localStorage.getItem("user_id");
  const { transactions, isLoading, error } = useTransactions(userId);
  const [searchTerm, setSearchTerm] = useState("");

  const filteredTransactions = transactions.filter(tx =>
    tx.currency_type.toLowerCase().includes(searchTerm.toLowerCase()) ||
    tx.transaction_type.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
      className="min-h-screen bg-gradient-to-br from-gray-800 to-black p-8 flex flex-col items-center"
    >
      <motion.button
        whileHover={{ scale: 1.05 }}
        whileTap={{ scale: 0.95 }}
        onClick={() => navigate("/dashboard")}
        className="bg-gradient-to-r from-blue-600 to-purple-700 hover:opacity-90 transition-transform px-6 py-3 rounded-full shadow-lg mb-6 text-lg font-semibold text-white flex items-center space-x-2"
      >
        <FaArrowLeft /> <span>Back to Dashboard</span>
      </motion.button>

      <h2 className="text-5xl font-extrabold text-center text-white mb-8">
        Transaction History
      </h2>

      <SearchBar searchTerm={searchTerm} setSearchTerm={setSearchTerm} />

      <TransactionSummary transactions={transactions} />

      <AnimatePresence>
        {isLoading ? (
          <LoadingSpinner />
        ) : (
          <>
            {error && (
              <p className="text-center mt-4 text-red-400">{error}</p>
            )}

            {filteredTransactions.length > 0 && (
              <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ delayChildren: 0.2, staggerChildren: 0.1 }}
                className="mt-8 w-full max-w-5xl grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8"
              >
                {filteredTransactions.map((tx) => (
                  <TransactionCard key={tx.transaction_id} transaction={tx} onClick={() => alert(`Transaction ID: ${tx.transaction_id}`)} />
                ))}
              </motion.div>
            )}
          </>
        )}
      </AnimatePresence>
    </motion.div>
  );
};

export default Transactions;
