import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { motion, AnimatePresence } from "framer-motion";
import { FaArrowLeft, FaCheckCircle, FaTimesCircle, FaSyncAlt } from "react-icons/fa";

const Transactions = () => {
  const navigate = useNavigate();
  const userId = localStorage.getItem("user_id");
  const [transactions, setTransactions] = useState([]);
  const [message, setMessage] = useState("Summoning Transaction Data...");
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!userId) {
      setMessage("‚ùå User Anomaly Detected. Please Authenticate.");
      setLoading(false);
      return;
    }
    fetchTransactions();
  }, [userId]);

  const fetchTransactions = async () => {
    setLoading(true);
    try {
      const response = await axios.post(
        "http://localhost/Crypto/get_transactions.php",
        { user_id: userId }
      );

      if (response.data.status === "success" && response.data.transactions.length > 0) {
        setTransactions(response.data.transactions);
        setMessage("");
      } else {
        setMessage("No Echoes of Transactions Found.");
      }
    } catch (error) {
      console.error("Error Fetching Temporal Data:", error);
      setMessage("‚ùå Temporal Data Retrieval Failure.");
    } finally {
      setLoading(false);
    }
  };

  const transactionVariants = {
    hidden: { opacity: 0, y: 50, scale: 0.95 },
    visible: { opacity: 1, y: 0, scale: 1, transition: { duration: 0.8, ease: [0.6, 0.05, -0.01, 0.9] } },
    hover: { scale: 1.05, boxShadow: "0 15px 35px rgba(0, 150, 255, 0.3)", transition: { duration: 0.3 } },
  };

  const loadingVariants = {
    hidden: { opacity: 0 },
    visible: { opacity: 1, transition: { duration: 0.7 } },
  };

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
      className="min-h-screen bg-gradient-to-br from-indigo-900 to-purple-900 text-white p-10 flex flex-col items-center relative overflow-hidden"
    >
      {/* Ethereal Background Waves */}
      <div className="absolute inset-0 pointer-events-none">
        <div className="absolute top-0 left-0 w-full h-full bg-gradient-to-r from-blue-500 via-purple-500 to-pink-500 opacity-20 animate-pulseBlur"></div>
        <div className="absolute top-1/4 left-1/4 w-1/2 h-1/2 rounded-full bg-gradient-radial from-purple-400 to-transparent opacity-30 animate-pulseGlow"></div>
        <div className="absolute bottom-0 left-0 w-full h-1/3 bg-gradient-to-b from-transparent to-indigo-700 opacity-30"></div>
      </div>

      <motion.button
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.9 }}
        onClick={() => navigate("/dashboard")}
        className="bg-gradient-to-r from-cyan-400 to-blue-500 hover:opacity-90 transition-transform px-8 py-3 rounded-full shadow-2xl mb-8 text-lg font-semibold tracking-wide flex items-center space-x-3 z-10"
      >
        <FaArrowLeft /> <span>Return to Nexus</span>
      </motion.button>

      <h2 className="text-6xl font-extrabold text-center bg-clip-text text-transparent bg-gradient-to-r from-cyan-300 via-blue-300 to-purple-300 drop-shadow-2xl tracking-widest animate-pulseEthereal mb-10 z-10">
        Chronicles of Transactions
      </h2>

      <AnimatePresence>
        {loading && (
          <motion.div
            variants={loadingVariants}
            initial="hidden"
            animate="visible"
            exit="hidden"
            className="flex items-center space-x-4 text-gray-300 mb-6 z-10"
          >
            <FaSyncAlt className="animate-spin text-3xl" />
            <span>Decrypting Temporal Records...</span>
          </motion.div>
        )}
      </AnimatePresence>

      {message && <p className="text-center mt-6 text-gray-300 z-10">{message}</p>}

      {transactions.length > 0 && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delayChildren: 0.3, staggerChildren: 0.15 }}
          className="mt-10 w-full max-w-5xl grid grid-cols-1 md:grid-cols-2 gap-10 z-10"
        >
          {transactions.map((tx) => (
            <motion.div
              key={tx.transaction_id}
              variants={transactionVariants}
              whileHover="hover"
              className="relative bg-gradient-to-br from-gray-800 to-gray-900 p-10 rounded-3xl shadow-3xl border border-gray-700 overflow-hidden group"
            >
              <div
                className={`absolute inset-0 rounded-3xl blur-3xl opacity-60 group-hover:opacity-90 transition-opacity duration-500 ${
                  tx.transaction_type === "Send"
                    ? "bg-red-600"
                    : tx.transaction_type === "Receive"
                    ? "bg-green-600"
                    : "bg-yellow-600"
                }`}
              ></div>

              <div
                className={`absolute top-6 right-6 px-5 py-3 flex items-center gap-3 rounded-full text-lg font-semibold backdrop-blur-lg bg-opacity-70 shadow-xl animate-bouncePulse hover:scale-115 transition-transform duration-300 ease-in-out
                  ${
                    tx.transaction_type === "Send"
                      ? "bg-red-700 text-white shadow-red-600/70"
                      : tx.transaction_type === "Receive"
                      ? "bg-green-700 text-white shadow-green-600/70"
                      : "bg-yellow-700 text-black shadow-yellow-600/70"
                  }`}
              >
                {tx.transaction_type === "Send" && <span>üì§</span>}
                {tx.transaction_type === "Receive" && <span>üì•</span>}
                {tx.transaction_type}
              </div>

              <p className="text-3xl font-bold text-gray-200">{tx.currency_type} Transaction</p>
              <p
                className={`font-semibold mt-3 flex items-center gap-3 text-lg ${
                  tx.status === "Completed" ? "text-green-300" : "text-red-300"
                }`}
              >
                {tx.status === "Completed" ? <FaCheckCircle /> : <FaTimesCircle />}
                <span>Status: {tx.status}</span>
              </p>
              <p className="text-4xl font-extrabold text-yellow-200 mt-3">
                <span className="text-xl">Œû</span> {tx.amount} {tx.currency_type}
              </p>
              <p className="text-gray-400 text-lg mt-3">
                <strong>Temporal Signature:</strong> {tx.timestamp}
              </p>
            </motion.div>
          ))}
        </motion.div>
      )}
    </motion.div>
  );
};

export default Transactions;
