import React, { useState, useEffect } from "react";
import axios from "axios";

const Transactions = () => {
    const [transactions, setTransactions] = useState([]);
    const [filters, setFilters] = useState({
        startDate: "",
        endDate: "",
        type: "",
        currency: "",
    });

    useEffect(() => {
        fetchTransactions();
    }, [filters]); // Fetch when filters change

    const fetchTransactions = async () => {
        try {
            const response = await axios.get("http://localhost/api/get_transactions.php", {
                params: filters, // Send filters as query params
            });
            setTransactions(response.data);
        } catch (error) {
            console.error("Error fetching transactions:", error);
        }
    };

    return (
        <div>
            <h2>Transaction History</h2>
            
            {/* Filter Section */}
            <div>
                <input type="date" onChange={(e) => setFilters({ ...filters, startDate: e.target.value })} />
                <input type="date" onChange={(e) => setFilters({ ...filters, endDate: e.target.value })} />
                
                <select onChange={(e) => setFilters({ ...filters, type: e.target.value })}>
                    <option value="">All Types</option>
                    <option value="Transfer">Transfer</option>
                    <option value="Deposit">Deposit</option>
                    <option value="Withdrawal">Withdrawal</option>
                </select>
                
                <select onChange={(e) => setFilters({ ...filters, currency: e.target.value })}>
                    <option value="">All Currencies</option>
                    <option value="BTC">BTC</option>
                    <option value="ETH">ETH</option>
                    <option value="USDT">USDT</option>
                </select>
                
                <button onClick={fetchTransactions}>Apply Filters</button>
            </div>

            {/* Display Transactions */}
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Type</th>
                        <th>Amount</th>
                        <th>Currency</th>
                        <th>Date</th>
                    </tr>
                </thead>
                <tbody>
                    {transactions.map((tx) => (
                        <tr key={tx.transaction_id}>
                            <td>{tx.transaction_id}</td>
                            <td>{tx.transaction_type}</td>
                            <td>{tx.amount}</td>
                            <td>{tx.currency_type}</td>
                            <td>{tx.timestamp}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Transactions;
