import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Transactions = () => {
  const navigate = useNavigate();
  const walletId = localStorage.getItem("wallet_id"); // Use wallet_id instead of user_id
  const [transactions, setTransactions] = useState([]);
  const [message, setMessage] = useState("Loading transactions...");

  useEffect(() => {
    if (!walletId) {
      setMessage("No wallet found. Please create one.");
      return;
    }
    fetchTransactions();
  }, [walletId]);

  const fetchTransactions = async () => {
    try {
      const response = await axios.post("http://localhost/Crypto/get_transactions.php", {
        wallet_id: walletId, // Send wallet_id instead of user_id
      });

      if (response.data.status === "success") {
        setTransactions(response.data.transactions);
        setMessage(response.data.transactions.length ? "" : "No transactions found.");
      } else {
        setMessage("No transactions found.");
      }
    } catch (error) {
      setMessage("❌ Error fetching transactions.");
    }
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white p-6">
      <button onClick={() => navigate("/dashboard")} className="bg-gray-700 px-4 py-2 rounded-lg mb-4">
        ⬅ Back to Dashboard
      </button>
      <h2 className="text-3xl font-bold text-center">Transaction History</h2>

      {message && <p className="text-center mt-4 text-gray-400">{message}</p>}

      {transactions.length > 0 && (
        <div className="mt-6 max-w-3xl mx-auto">
          {transactions.map((tx) => (
            <div key={tx.transaction_id} className="bg-gray-800 p-4 rounded-lg mb-4 shadow">
              <p><strong>Type:</strong> {tx.transaction_type}</p>
              <p><strong>Status:</strong> {tx.status}</p>
              <p><strong>Amount:</strong> ${tx.amount}</p>
              <p className="text-gray-400 text-sm"><strong>Time:</strong> {tx.timestamp}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Transactions;
