import { useState, useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { motion, AnimatePresence } from "framer-motion";
import { FaArrowLeft, FaCheckCircle, FaTimesCircle, FaSyncAlt } from "react-icons/fa";

const Transactions = () => {
  const navigate = useNavigate();
  const userId = localStorage.getItem("user_id");
  const [transactions, setTransactions] = useState([]);
  const [message, setMessage] = useState("Initializing Quantum Data Stream...");
  const [loading, setLoading] = useState(true);
  const backgroundRef = useRef(null);

  useEffect(() => {
    if (!userId) {
      setMessage("User Quantum Signature Required.");
      setLoading(false);
      return;
    }
    fetchTransactions();
  }, [userId]);

  const fetchTransactions = async () => {
    setLoading(true);
    try {
      const response = await axios.post(
        "http://localhost/Crypto/get_transactions.php",
        { user_id: userId }
      );

      if (response.data.status === "success" && response.data.transactions.length > 0) {
        setTransactions(response.data.transactions);
        setMessage("");
      } else {
        setMessage("No Quantum Transactions Detected.");
      }
    } catch (error) {
      console.error("Quantum Data Stream Error:", error);
      setMessage("Quantum Data Retrieval Failure.");
    } finally {
      setLoading(false);
    }
  };

  const transactionVariants = {
    hidden: { opacity: 0, y: 50, scale: 0.95 },
    visible: { opacity: 1, y: 0, scale: 1, transition: { duration: 0.8, ease: [0.6, 0.05, -0.01, 0.9] } },
    hover: { scale: 1.03, boxShadow: "0 10px 30px rgba(100, 200, 255, 0.3)", transition: { duration: 0.3 } },
  };

  const loadingVariants = {
    hidden: { opacity: 0 },
    visible: { opacity: 1, transition: { duration: 0.7 } },
  };

  const handleMouseMove = (e) => {
    if (backgroundRef.current) {
      const x = e.clientX / window.innerWidth;
      const y = e.clientY / window.innerHeight;
      backgroundRef.current.style.transform = `translate(${x * 30}px, <span class="math-inline">\{y \* 30\}px\) rotate\(</span>{x * 10}deg)`;
    }
  };

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
      className="min-h-screen bg-gradient-to-br from-indigo-900 to-black text-white p-10 relative overflow-hidden"
      onMouseMove={handleMouseMove}
    >
      <div
        ref={backgroundRef}
        className="absolute inset-0 bg-gradient-to-r from-purple-900 via-blue-800 to-cyan-900 opacity-30 transition-transform duration-700 ease-out blur-xl"
      />

      <motion.button
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.9 }}
        onClick={() => navigate("/dashboard")}
        className="bg-gradient-to-r from-cyan-500 to-blue-600 hover:opacity-90 transition-transform px-8 py-3 rounded-full shadow-2xl mb-8 text-lg font-semibold flex items-center space-x-3 z-10"
      >
        <FaArrowLeft /> <span>Return to Quantum Nexus</span>
      </motion.button>

      <h2 className="text-5xl md:text-6xl lg:text-7xl font-extrabold text-center bg-clip-text text-transparent bg-gradient-to-r from-cyan-300 via-blue-300 to-purple-300 drop-shadow-2xl tracking-widest animate-pulseEthereal mb-10 z-10">
        Quantum Transaction Logs
      </h2>

      <AnimatePresence>
        {loading && (
          <motion.div
            variants={loadingVariants}
            initial="hidden"
            animate="visible"
            exit="hidden"
            className="flex items-center space-x-4 text-gray-300 mb-6 z-10"
          >
            <FaSyncAlt className="animate-spin text-3xl" />
            <span>Deciphering Quantum Data...</span>
          </motion.div>
        )}
      </AnimatePresence>

      {message && <p className="text-center mt-6 text-gray-300 z-10">{message}</p>}

      {transactions.length > 0 && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delayChildren: 0.3, staggerChildren: 0.15 }}
          className="mt-10 w-full max-w-5xl grid grid-cols-1 md:grid-cols-2 gap-10 z-10"
        >
          {transactions.map((tx) => (
            <motion.div
              key={tx.transaction_id}
              variants={transactionVariants}
              whileHover="hover"
              className="relative bg-gradient-to-br from-gray-900 to-black p-10 rounded-3xl shadow-3xl border border-gray-800 overflow-hidden group"
            >
              <div
                className={`absolute inset-0 rounded-3xl blur-3xl opacity-60 group-hover:opacity-90 transition-opacity duration-500 ${
                  tx.transaction_type === "Send"
                    ? "bg-red-600"
                    : tx.transaction_type === "Receive"
                    ? "bg-green-600"
                    : "bg-yellow-600"
                }`}
              ></div>

              <div
                className={`absolute top-6 right-6 px-5 py-3 flex items-center gap-3 rounded-full text-lg font-semibold backdrop-blur-lg bg-opacity-70 shadow-xl animate-bouncePulse hover:scale-115 transition-transform duration-300 ease-in-out
                  ${
                    tx.transaction_type === "Send"
                      ? "bg-red-700 text-white shadow-red-600/70"
                      : tx.transaction_type === "Receive"
                      ? "bg-green-700 text-white shadow-green-600/70"
                      : "bg-yellow-700 text-black shadow-yellow-600/70"
                  }`}
              >
                {tx.transaction_type === "Send" && <span>📤</span>}
                {tx.transaction_type === "Receive" && <span>📥</span>}
                {tx.transaction_type}
              </div>

              <p className="text-3xl font-bold text-gray-200">{tx.currency_type}Transactions</p>
              <p
                className={`font-semibold mt-3 flex items-center gap-3 text-lg ${
                  tx.status === "Completed" ? "text-green-300" : "text-red-300"
                }`}
              >
                {tx.status === "Completed" ? <FaCheckCircle /> : <FaTimesCircle />}
                <span>Status: {tx.status}</span>
              </p>
              <p className="text-4xl font-extrabold text-yellow-200 mt-3">
                <span className="text-xl">Ξ</span> {tx.amount} {tx.currency_type}
              </p>
              <p className="text-gray-400 text-lg mt-3">
                <strong>Quantum Temporal Signature:</strong> {tx.timestamp}
              </p>
            </motion.div>
          ))}
        </motion.div>
      )}
    </motion.div>
  );
};

export default Transactions;
